import { Box } from '@mui/material';

import startIcon from '../../assets/img/starIcon.png'

const InformationCard = ({ superHost, type, title, beds, rating }) => {

    if (superHost === false) {
      (superHost = null)
    } else if 
      (superHost === true) {
      (superHost = <Box
        component="span"
        sx={{
          fontSize: '14px',
          fontFamily: 'Monserrate',
          border: '1px solid #D7137F',
          borderRadius: '25px',
        }}
      >SUPER HOST</Box>)
    }

    return (
      <Box
        sx={{
          height: '50px',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-beetween',
          alignContent: 'center',
          alignItems:'flex-start',
        }}
      >
        <div className="Information-card-container">
            <span className="Information-key">
              {superHost}
              {type}
              {beds > 0 && <>. {beds} beds</>}
              <img src={startIcon} alt="Rating with start icon" srcset="" />{rating}
            </span>
            <div className="rating-container">
          </div>
        </div>
        <div className="title">
          {title}
        </div>
      </Box>
    );
  };

  export default InformationCard;
