import Home from "./components/Home/Home";
import Card from "./components/Card";
import Filter from "./components/Filter/Filter";
import Stays from "./components/Stays/Stays";

import { useState, useEffect } from "react";
import { data } from "./stays.json";

import'./App.css';

function App() {
  const [location, setLocation] = useState("");
  const [beds, setBeds] = useState(data);
  const [adultCounter, setAdultCounter] = useState(0);
  const [childrenCounter, setChildrenCounter] = useState(0);
  const handleLocation = (e) => setLocation(e.target.value);

  const [isFiltered, setFiltered] = useState(false);
  const handleModal = () => {
    setFiltered(true);
  };    
  const closeModal = () => {
    setFiltered(false);
  };

  useEffect(() => {
    const search = data.filter(
      (cards) =>
        cards.city.toLowerCase().includes(location.toLowerCase()) 
         && cards.maxGuests >= Number(counterAdult + counterChildren)
    );

    setRooms(search);
  }, [location, adultCounter, childrenCounter]);

  return (
    <div className="App">
      <div>
        <Home onClickMod={handleModal} />
        {isFiltered && (
          <Filter
            location={location}
            handleLocation={handleLocation}
            onClickNav={setLocation}
            offModal={closeModal}
            adultsTotal={adultCounter}
            childrenTotal={counterChildren}
            adultsLess={() => setAdultCounter(counterAdult - 1)}
            adultsPlus={() => setAdultCounter(counterAdult + 1)}
            childrenLess={() => setChildrenCounter(counterChildren - 1)}
            childrenPlus={() => setChildrenCounter(counterChildren + 1)}
