          {rooms.map((cards) => (
            <Card
              key={cards.id}
              image={cards.photo}
              superHost={cards.superHost}
              type={cards.type}
              beds={cards.beds}
              rating={cards.rating}
              title={cards.title}
            />
          ))}
        </section>

      </div>
    </div>
  );
}

export default App;
